class RedmineauthController < ApplicationController
  before_filter :configured?
  def index
    redirect_to :action => 'login'
  end

  def login
    if request.post?
      redmine_user = RedmineUser.new(params[:login][:key])
      unless redmine_user.exist?
        flash[:error] = 'login failed'
        return
      end
      
      users = User.select do |record|
        record['screen_name'] == redmine_user.screen_name
      end
      user = (users.records.size > 0 ? users.first : User.new)
      user.screen_name ||= redmine_user.name
      user.name ||= redmine_user.screen_name
      user.profile_image_url = 'data:image/gif;base64,'
      user.save
      session[:current_user_id] = user.id
      redirect_to :controller => 'chat', :action => 'index'
    end

  end

  private
  def configured?
    unless Setting[:login_link]
      logger.info "not configured!!"
      render :file => 'public/404.html'
    end
  end
end
